import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [input, setInput] = useState("");
  const [response, setResponse] = useState("");

  const handleInputSubmit = async () => {
    try {
      // Make a POST request to the local Flask backend
      const result = await axios.post(
        "http://127.0.0.1:5000/api/generate",
        {
          prompt: input,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      setResponse(result.data);
    } catch (error) {
      console.error("Error communicating with local backend API", error);
      console.error("Error details:", error.response?.data || error.message);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h1>Golf App AI Assistant</h1>
      <textarea
        style={{ width: "100%", height: "100px" }}
        placeholder="Enter your round details..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={handleInputSubmit} style={{ marginTop: "10px" }}>
        Submit
      </button>
      {response && (
        <div
          style={{
            marginTop: "20px",
            background: "#f0f0f0",
            padding: "10px",
            borderRadius: "5px",
          }}
        >
          <h2>AI Response:</h2>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default App;
